const getTemplate = (data = [], placeholder, selectedId) => {
    let text = placeholder ?? 'Default placeholder'

    const items = data.map(item => {
        let cls = ''

        if (item.id === selectedId) {
            text = item.value
            cls = '_selected'
        }
        return `<li class="select__item ${cls}" data-type="item" data-id="${item.id}">${item.value}</li>`
    })

    return `
        <div class="select__backdrop" data-type="backdrop"></div>

        <div class="select__input" data-type="input">
            <span data-type="placeholder">${text}</span> 
            <i class="fa fa-chevron-down" data-type="arrow"></i>
        </div>

        <div class="select__dropdown">
            <ul class="select__list">
                ${items.join(' ')}
            </ul>
        </div>
    `
}

export class Select {
    constructor(selector, options) {
        this.$element = document.querySelector(selector)
        this.options = options
        this.selectedId = options.selectedId

        this.#render()
        this.#setup()
    }

    #render() {
        const {placeholder, data} = this.options

        this.$element.classList.add('select')
        this.$element.innerHTML = getTemplate(data, placeholder, this.selectedId)
    }

    #setup() {
        this.clickHandler = this.clickHandler.bind(this)
        this.$element.addEventListener('click',this.clickHandler)
        this.$arrow = this.$element.querySelector('[data-type="arrow"]')
        this.$placeholder = this.$element.querySelector('[data-type="placeholder"]')
    }

    clickHandler(event) {
        const {type} = event.target.dataset

        if (type === 'input') {
            this.toggle()
        } else if (type === 'item') {
            const id = event.target.dataset.id
            this.select(id)
        } else if (type === 'backdrop') {
            this.close()
        }
    }

    get isOpen() {
        return this.$element.classList.contains('_open')
    }

    get current() {
        return this.options.data.find(item => item.id === this.selectedId)
    }

    select(id) {
        this.selectedId = id
        this.$placeholder.textContent = this.current.value

        this.$element.querySelectorAll(`[data-type="item"]`).forEach(item => {
            item.classList.remove('_selected')
        })

        this.$element.querySelector(`[data-id="${id}"]`).classList.add('_selected')
        this.options.onSelect ? this.options.onSelect(this.current) : null
        this.close()
    }

    toggle() {
        this.isOpen ? this.close() : this.open()
    }

    open() {
        this.$element.classList.add('_open')
        this.$arrow.classList.remove('fa-chevron-down')
        this.$arrow.classList.add('fa-chevron-up')
    }

    close() {
        this.$element.classList.remove('_open')
        this.$arrow.classList.add('fa-chevron-down')
        this.$arrow.classList.remove('fa-chevron-up')
    }

    destroy() {
        this.$element.removeEventListener('click', this.clickHandler)
        this.$element.innerHTML = ''
    }
}
